class VdMessage{constructor(){this.iconMap={success:'<i class="bi bi-check-circle-fill"></i>',warning:'<i class="bi bi-exclamation-circle-fill"></i>',error:'<i class="bi bi-x-circle-fill"></i>',info:'<i class="bi bi-info-circle-fill"></i>'},this.messages=[],this.maxMessages=2,this.insertMessageContainer(),this.insertCSS()}insertMessageContainer(){if(0===$(".vd_message_js").length){const s=$('<div class="vd_message_js"></div>');$("body").append(s)}}insertCSS(){0===$("#vd_message_style").length&&$('<style id="vd_message_style"></style>').text("\n            .vd_message_js {\n            display: flex;\n            flex-direction: column;\n            align-items: center;\n            position: fixed;\n            top: 10px;\n            left: 50%;\n            transform: translateX(-50%);\n            z-index: 2010;\n            transition: 0.3s;\n            position: fixed;\n            }\n\n            .vd_message {\n            padding: 9px 12px;\n            border-radius: 12px;\n            box-shadow: 0 6px 16px 0 rgba(0, 0, 0, 0.08),\n                0 3px 6px -4px rgba(0, 0, 0, 0.12), 0 9px 28px 8px rgba(0, 0, 0, 0.05);\n            background: #ffffff;\n            display: flex;\n            justify-content: center;\n            align-items: center;\n            margin-bottom: 10px;\n            transition: 0.3s;\n            width: auto;\n            position: absolute;\n            white-space: nowrap;\n            }\n\n            .vd_message_main {\n            display: flex;\n            gap: 5px;\n            align-items: center;\n            }\n\n            .vd_message_noti {\n            color: rgba(0, 0, 0, 0.88);\n            font-size: 14px;\n            line-height: 1.5714285714285714;\n            list-style: none;\n            pointer-events: none;\n            }\n\n            /* status */\n            .vd_message.success .vd_message_icon {\n            color: #52c41a;\n            }\n\n            .vd_message.error .vd_message_icon {\n            color: red;\n            }\n\n            .vd_message.warning .vd_message_icon {\n            color: orange;\n            }\n\n            .vd_message.info .vd_message_icon {\n            color: #0d6efd;\n            }\n      ").appendTo("head")}createMessage(s,e){return`\n        <div class="vd_message ${s} animate__animated animate__faster animate__fadeInDown">\n          <div class="vd_message_main">\n            <div class="vd_message_icon">\n              ${this.iconMap[s]||this.iconMap.info}\n            </div>\n            <div class="vd_message_noti">\n              ${e}\n            </div>\n          </div>\n        </div>\n      `}show(s,e,n=3e3){const i=this.createMessage(s,e),a=$(".vd_message_js");if(this.messages.length>=this.maxMessages){this.messages.shift().remove()}const t=$(i);this.messages.push(t),a.append(t),this.updateMessagePositions(),t.fadeIn(5,(function(){setTimeout((()=>{$(this).removeClass("animate__fadeInDown").addClass("animate__fadeOutUp"),setTimeout((()=>{$(this).remove()}),1e3)}),n)}))}updateMessagePositions(){$(".vd_message_js").children().each((function(s){$(this).css({top:3.2*s+"rem",position:"absolute"})}))}destroy(){this.messages.forEach((s=>{s.removeClass("animate__fadeInDown").addClass("animate__fadeOutUp"),setTimeout((()=>{s.remove()}),1e3)}))}}

